from django.contrib import adminfrom recipes.models import IngredientModel, StepModel, RecipeModelclass IngredientInline(admin.StackedInline):    model = IngredientModelclass StepInline(admin.StackedInline):    model = StepModelclass RecipeAdmin(admin.ModelAdmin):    inlines = [IngredientInline, StepInline]    raw_id_fields = ('user',)    list_display = ('name', 'user')    list_filter = ['user__username',]    search_fields = ['user__username', 'name']admin.site.register(RecipeModel, RecipeAdmin)