from django.db import modelsfrom django.contrib.auth.models import Userclass RecipeModel(models.Model):    name = models.CharField(max_length=256)    user = models.OneToOneField(User, on_delete=models.CASCADE)    class Meta:        ordering = ('name',)        verbose_name = 'Recipe'        verbose_name_plural = 'Recipes'class IngredientModel(models.Model):    recipe = models.ForeignKey(RecipeModel, on_delete=models.CASCADE, related_name='ingredients')    ingredient_text = models.CharField(max_length=256, verbose_name='Ingredient')    class Meta:        ordering = ('recipe',)        verbose_name = 'Ingredient'        verbose_name_plural = 'Ingredients'class StepModel(models.Model):    recipe = models.ForeignKey(RecipeModel, on_delete=models.CASCADE, related_name='steps')    step_text = models.CharField(max_length=256, verbose_name='Step')    class Meta:        ordering = ('recipe',)        verbose_name = 'Step'        verbose_name_plural = 'Steps'