from django.urls import pathfrom django.views.generic import TemplateViewfrom recipes.views import CreateRecipeView, ViewAllRecipes, RecipeDelete, ViewRecipeurlpatterns = [    path('recipe_delete/<int:pk>/', RecipeDelete.as_view(), name='delete-recipe'),    path('view_recipe/<int:pk>/', ViewRecipe.as_view(), name='view-recipe'),    path('recipe_list/', ViewAllRecipes.as_view(), name='view-all-recipes'),    path('new-recipe-success/',TemplateView.as_view(template_name='recipes/create_recipe_success.html'),         name='recipe-success'),    path('create_new_recipe', CreateRecipeView.as_view(), name="create-new-recipe"),    path('', TemplateView.as_view(template_name='recipes/base.html'), name='recipe-dashboard'),]